import '../style/TodoListDate.css';

const TodoListDate = () => {

    const today = new Date();

    const dateString = today.toLocaleString('ko-KR', {
        year: 'numeric',
        month: 'long',
        day: 'numeric'
    });

    const dayName = today.toLocaleString('ko-KR', { weekday: 'long' });

	return (
        <div className='todolist-date'>
            <h3>{dateString}</h3>
            <div>{dayName}</div>
        </div>
    )
}

export default TodoListDate;
