import React, { useEffect, useState } from "react";
import axios from 'axios';

import '../style/Board.css';

const Board = () => {

    const [boarddata, setBoarddata] = useState([{
        boardnumber: '',
        boardtitle: '',
        boardtext: ''
    }]);

    const dbtablemake = () => {
        axios
        .get('/prj05/cdbt')
        .then(res => 
            console.log(res.data)
        );
    };

    const dbtableinsert = () => {
        axios
        .get('/prj05/cdbi')
        .then(res => {
            console.log(res.data);
        });
    };


    useEffect(async () => {
        try {
            const res = await axios.get('/prj05/cdbs')

            const data = await res.data.map((datas) => ({
                    boardnumber: datas.board_number,
                    boardtitle: datas.board_title,
                    boardtext: datas.board_text
                })
            )
            setBoarddata(boarddata.concat(data))
        } 

        catch(e) {
            console.error(e.message)
        }
    }, [])

    return (
        <>
            <div className='board-outer'>

                <div className='board-inner-sidemenu'>
                    <button className='board-inner-sidemenu-button' onClick={dbtablemake}>
                        DB에 테이블 생성하기
                    </button>

                    <button className='board-inner-sidemenu-button' onClick={dbtableinsert}>
                        게시판 임시데이터 삽입
                    </button>

                </div>

                <div className='board-inner-boardcontents'>

                    {/* {data && data.map(datas => {
                        return (
                            <>
                                <div className='board-inner-boardcontents-title' key={data.BOARD_NUMBER}>
                                    {data.BOARD_TITLE}
                                </div>

                                <div className='board-inner-boardcontents-context'>
                                    {data.BOARD_CONTEXT}
                                </div>
                            </>
                        );
                    })} */}

                </div>

            </div>
        </>
    )
}

export default Board;
