import { createContext, useContext, useEffect, useState } from "react";



const ContextProvider = (chileren) => {

    // 전역 상태 관리를 위한 ContextAPI 사용을 위해 createContext()을 사용.
    const Context = createContext();

    // 로그인 여부 값과 로그인 한 사용자 이름을 관리하기 위한 isLogin / whoLogin 변수를 useState로 관리.
    const [isLogin, setIsLogin] = useState(false);
    const [whoLogin, setWhoLogin] = useState('익명사용자');

    useEffect(() => {
        // sessionStorage에 Item으로 담겨진 로그인값을 검증.
        // 로그인 값의 이름은 'MEMBER_ID', 이 값이 없을(null) 경우 아무 일도 일어나지 않는다.
        if (sessionStorage.getItem('MEMBER_ID') === null) {
            
        } 
        else {
        // 이 값이 있을 경우 useState로 제어하는 isLogin의 값을 true로 전환한다.
        // 하위 컴포넌트에서 로그인 값을 필요로 할 경우, 여기서 전달해주면 된다.
            setIsLogin(true);
            setWhoLogin(sessionStorage.getItem('MEMBER_ID'));
        }
    }, [isLogin]) 

	return (
        <Context.Provider>
            {chileren}
        </Context.Provider>
    )
}

export function useContextProvider() {
    return useContext(Context);
};
