import React, { useCallback, useContext, useState } from 'react';
import axios from 'axios';

import { myContext } from '../App';

import '../style/TodoListAdd.css';
import '../style/GlobalStyle.css';

const TodoListAddDB = ( ) => {
    // contextAPI를 사용.
    const contextApi = useContext(myContext);

    // 사용자 ID를 제어하는 변수.
    const [memberID, setmemberID] = useState("");   

    // 입력한 값을 제어하는 변수.
    const [inputText, setinputText] = useState("");

    const onChange = useCallback(e => {
        setinputText(e.target.value);
    }, []);

    const addTodolist = () => {
        // 로그인 검증 실시.
        contextApi.loginCheck();

        if(!contextApi.loginStatus)
            console.log("회원정보 검증이 실패하였습니다.");

        setmemberID(contextApi.whoislogin);

        axios
        .post('/prj05/todo/inserttodo', {
            params: {
                'TODO_MEMBER': memberID,
                'TODO_TEXT': inputText
            }
        })
        .then(res => {
            console.log(res.data.SystemMassage);
        })
        .catch(res => {
            console.log(res.data.SystemMassage);
        })
    }

    return (
        <>
            <div className='tdl-addform gifont'>
                <div className="tdl-form">
                    <input className="tdl-addinput gifont" placeholder="추가할 할 일을 입력하세요" defaultValue={inputText} onChange={onChange}/>
                    <button className="tdl-addbtu gifont" onClick={addTodolist}>
                        <h2>추가</h2>
                    </button>
                </div>
            </div>
        </>
    )
}

export default TodoListAddDB;
