import { useState } from 'react';
import TodoListAdd from '../components/todoListAdd';
import TodoListBox from '../components/todoListBox';
import TodoListDate from '../components/todoListDate';
import '../style/Todolist.css';

const Todolist = () => {

    const [todos, setTodos] = useState([
        {
        id: 1,
        text: '리액트 공부하기',
        checked: true,
        star: false,
        },
        {
        id: 2,
        text: '도서관가서 책 빌리기',
        checked: true,
        star: false,
        },
        {
        id: 3,
        text: '방 청소하기',
        checked: false,
        star: true,
        },
    ]);
    
    const nextId = useRef(4);
    
    const onAdd = useCallback(
        text => {
        const todo = {
            id: nextId.current,
            text,
            checked: false,
        };
        setTodos(todos.concat(todo));
        nextId.current++;
        },
        [todos]
    );

    return (
        <>
            <div className='todolist-outer'>
                <div className='todolist-inner todolist-section'>
                    <div className='todolist-contentsbox'>

                        <h1>TodoList</h1>

                        <div className='todolist-listouter'>
                            <div className='todolist-menu'>
                                <TodoListDate onAdd={onAdd}/>
                            </div>

                            <div className='todolist-contents'>
                                <TodoListAdd/>

                                <TodoListBox>

                                </TodoListBox>
                            </div>

                        </div>
                    </div>
                </div>
            </div>
        </>
    )
}

export default Todolist;
