import { useState } from 'react';

import '../style/MemberJoin.css';

const MemberJoin = () => {

    const [inputID, setInputID] = useState('')
    const [inputPW, setInputPW] = useState('')
    const [inputName, setInputName] = useState('')
    const [inputEMail, setInputEMail] = useState('')

    // 정확한 입력값을 체크하기 위해서 입력창의 값이 변하는 순간마다 값을 갱신, useState로 변수에 SET하도록 함.
    const handleInputID = (e) => {
        setInputID(e.target.value)
    }
    const handleInputPW = (e) => {
        setInputPW(e.target.value)
    }
    const handleInputName = (e) => {
        setInputName(e.target.value)
    }
    const handleInputEMail = (e) => {
        setInputEMail(e.target.value)
    }
    
    return (
        <>
            <div className='join-outer join-section'>
                <div className='join-inner join-section'>

                    <div className='join-joinform'>

                        <div className='join-joinform-title'>
                            <h2>회원가입</h2>
                        </div>

                        <div className='join-joinform-input'>
                            <div className='join-joinform-labelinput'>
                                <label htmlFor='inputid'>아이디 : </label>
                                <input type='text' name='inputid' value={inputID} onChange={handleInputID} />
                            </div>

                            <div className='join-joinform-labelinput'>
                                <label htmlFor='inputid'>비밀번호 : </label>
                                <input type='text' name='inputid' value={inputPW} onChange={handleInputPW} />
                            </div>

                            <div className='join-joinform-labelinput'>
                                <label htmlFor='inputid'>이름 : </label>
                                <input type='text' name='inputid' value={inputName} onChange={handleInputName} />
                            </div>

                            <div className='join-joinform-labelinput'>
                                <label htmlFor='inputid'>이메일 주소 : </label>
                                <input type='text' name='inputid' value={inputEMail} onChange={handleInputEMail} />
                            </div>
                        </div>


                        <div className='login-loginform-submitbutton'>
                            <div className='login-loginform-submitbtu'>
                                <button type='button' onClick={''}>회원가입</button>
                            </div>
                        </div>
                        
                    </div>





                </div>
            </div>
        </>
    )
}

export default MemberJoin;
