import { BrowserRouter, Routes, Route } from 'react-router-dom';
import { useEffect, useState } from 'react';

import './App.css';

import Menubar from './pages/menubar';
import Main from './pages/main';
import Callapi from './pages/callapi';
import Todolist from './pages/todolist';
import Board from './pages/board';
import Member from './pages/member';

function App() {

  const [isLogin, setIsLogin] = useState(false);
  const [whoLogin, setWhoLogin] = useState('');

  useEffect(() => {
      // sessionStorage에 Item으로 담겨진 로그인값을 검증.
      // 로그인 값의 이름은 'MEMBER_ID', 이 값이 없을(null) 경우 아무 일도 일어나지 않는다.
      if (sessionStorage.getItem('MEMBER_ID') === null) {

      } 
      else {
      // 이 값이 있을 경우 useState로 제어하는 isLogin의 값을 true로 전환한다.
      // 하위 컴포넌트에서 로그인 값을 필요로 할 경우, 여기서 전달해주면 된다.
          setIsLogin(true);
          setWhoLogin(sessionStorage.getItem('MEMBER_ID'));
      }
  }, [isLogin])

  return (
    <>
      <BrowserRouter>
        <Menubar isLogin={isLogin}/>
        <Routes>
          <Route path='/' element={<Main/>}/>
          <Route path='/api' element={<Callapi/>}/>
          <Route path='/todolist' element={<Todolist/>}/>
          <Route path='/board' element={<Board isLogin={isLogin} whoLogin={whoLogin}/>}/>
          <Route path='/member' element={<Member isLogin={isLogin} whoLogin={whoLogin}/>}/>
        </Routes>
      </BrowserRouter>
    </>
  );
}

export default App;
