import axios from 'axios';
import { useEffect, useState } from 'react';

import '../style/MemberInfo.css';

const MemberInfo = (props) => {

    const [memberdata, setMemberdata] = useState([]);

    useEffect(() => {
        axios
        .get('/prj05/member/info', {
            params: {
                'MEMBER_ID': props.whoLogin,
            }
        })
        .then(res => {
            setMemberdata(res.data);
            console.log(res.data);
        });
    
    }, [props.whoLogin])

    return (
        <>
            <div className='memberinfo-outer memberinfo-section'>
                <div className='memberinfo-inner memberinfo-section'>
                    <div className='memberinfo-memberinfo'>
                        <div className='memberinfo-memberinfo-title'>
                            <h2>로그인</h2>
                        </div>
                        <div className='memberinfo-memberinfo-input'>
                            <div className='memberinfo-memberinfo-idpwinput'>
                                <label htmlFor='inputid'>ID : </label>
                                <input type='text' name='inputid' value={inputID} onChange={handleInputId} />
                            </div>
                            <div className='memberinfo-memberinfo-idpwinput'>
                                <label htmlFor='inputpw'>PW : </label>
                                <input type='password' name='inputpw' value={inputPW} onChange={handleInputPw} />
                            </div>
                        </div>
                        <div className='memberinfo-memberinfo-submitbutton'>
                            <div className='memberinfo-memberinfo-submitbtu'>
                                <button type='button' onClick={onClickLogin}>Login</button>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        </>
    )
}

export default MemberInfo;
