import { BrowserRouter, Routes, Route } from 'react-router-dom';
import { useContext, useEffect } from 'react';

// import { useEffect, useState } from 'react';

import './App.css';

import Menubar from './pages/menubar';
import Main from './pages/main';
import Callapi from './pages/callapi';
import Todolist from './pages/todolist';
import Board from './pages/board';
import Boardview from './pages/boardview';
import Member from './pages/member';

import StateManager, { LoginContext } from './pages/stateManager';

function App() {

  // const [isLogin, setIsLogin] = useState(false);
  // const [whoLogin, setWhoLogin] = useState('익명사용자');

  const logininfo = useContext(StateManager);

  useEffect(() => {
    // 웹이 실행되면 가장 먼저 isLogin을 flase로 초기화.
      logininfo.setIsLogin(false);

    // sessionStorage에 Item으로 담겨진 로그인값을 검증.
    // 로그인 값의 이름은 'MEMBER_ID', 이 값이 없을(null) 경우 whoLogin의 값을 '익명사용자'로 set.
    if (sessionStorage.getItem('MEMBER_ID') === null) {
      logininfo.setWhoLogin('익명사용자');
    } 
    else {
    // 이 값이 있을 경우 useState로 제어하는 isLogin의 값을 true로 전환하고 whoLogin을 로그인 한 유저의 ID값으로 변경한다.
      logininfo.setIsLogin(true);
      logininfo.setWhoLogin(sessionStorage.getItem('MEMBER_ID'));
    }
}, [logininfo])

  return (
    // <ContextProvider>
    //   <BrowserRouter>
    //     <Menubar isLogin={isLogin}/>
    //     <Routes>
    //       <Route path='/' element={<Main/>}/>
    //       <Route path='/api' element={<Callapi/>}/>
    //       <Route path='/todolist' element={<Todolist/>}/>
    //       <Route path='/board' element={<Board isLogin={isLogin} whoLogin={whoLogin}/>}/>
    //       <Route path='/boardview/:BOARD_NUMBER' element={<Boardview isLogin={isLogin} whoLogin={whoLogin}/>}/>
    //       <Route path='/member' element={<Member isLogin={isLogin} whoLogin={whoLogin}/>}/>
    //     </Routes>
    //   </BrowserRouter>
    // </ContextProvider>

    <StateManager>
      <LoginContext.Provider value={{ logininfo }}>
        <BrowserRouter>
          <Menubar/>
          <Routes>
            <Route path='/' element={<Main/>}/>
            <Route path='/api' element={<Callapi/>}/>
            <Route path='/todolist' element={<Todolist/>}/>
            <Route path='/board' element={<Board/>}/>
            <Route path='/boardview/:BOARD_NUMBER' element={<Boardview/>}/>
            <Route path='/member' element={<Member/>}/>
          </Routes>
        </BrowserRouter>
      </LoginContext.Provider>
    </StateManager>
  );
}

export default App;
